        /// 插入，更新操作
        /// 
        /// 已开启事务
        /// 
        /// 
        /// 
        /// 
        public int ExecuteNonQuery(params string[] sqls)
        {
            if (!sqls.Any() || sqls.All(string.IsNullOrEmpty))
            {
                return 0;
            }

            int rows = 0;
            SQLiteTransaction tran = null;

            try
            {
                readerWriterLockSlim.TryEnterWriteLock(timeOutSecond);

                using (var con = new SQLiteConnection(conStr))
                {
                    con.Open();
                    tran = con.BeginTransaction();
                    foreach (string sql in sqls)
                    {
                        rows += con.Execute(sql, tran);
                    }
                    tran.Commit();
                }

            }
            catch (Exception ex)
            {
                tran?.Rollback();
                throw ex;
            }
            finally
            {
                readerWriterLockSlim.ExitWriteLock();
            }

            return rows;
        }

        /// 
        /// 执行查询，并返回查询所返回的结果集中第一行的第一列
        /// 忽略其他行或列，返回值为object类型
        /// 
        /// 查询语句
        /// 参数
        /// 是否为查询操作
        /// 
        public T ExecuteScalar(string sql, object param = null, bool isQuery = true)
        {
            try
            {
                if (isQuery)
                {
                    readerWriterLockSlim.TryEnterReadLock(timeOutSecond);
                }
                else
                {
                    readerWriterLockSlim.TryEnterWriteLock(timeOutSecond);
                }
                using (var con = new SQLiteConnection(conStr))
                {
                    var ret = con.ExecuteScalar(sql, param);
                    return ret;
                }

            }
            catch (Exception e)
            {
                throw e;
            }
            finally
            {
                if (isQuery)
                {
                    readerWriterLockSlim.ExitReadLock();
                }
                else
                {
                    readerWriterLockSlim.ExitWriteLock();
                }
            }
        }

        /// 
        /// 查询
        /// 
        /// 
        /// select * from tableName where colName=@colName"
        /// 
        /// 返回类型
        /// 查询语句
        /// 参数
        /// 
        public IEnumerable Query(string sql, object param = null)
            where T : class
        {
            try
            {
                readerWriterLockSlim.TryEnterReadLock(timeOutSecond);
                var con = new SQLiteConnection(conStr);
                IEnumerable ret = con.Query(sql, param);
                return ret;
            }
            catch (Exception e)
            {
                throw e;
            }
            finally
            {
                readerWriterLockSlim.ExitReadLock();
            }
        }

        /// 
        /// 查询
        /// 
        /// 返回类型
        /// 查询语句
        /// 参数
        /// 返回第一行数据
        public T QueryFirstOrDefault(string sql, object param = null)
            where T : class
        {
            try
            {
                readerWriterLockSlim.TryEnterReadLock(timeOutSecond);
                var con = new SQLiteConnection(conStr);
                T ret = con.QueryFirstOrDefault(sql, param);
                return ret;
            }
            catch (Exception e)
            {
                throw e;
            }
            finally
            {
                readerWriterLockSlim.ExitReadLock();
            }
        }

        /// 
        /// 查询返回DataTable
        /// 
        /// 
        public DataTable QueryForDataTable(string sql)
        {
            try
            {
                readerWriterLockSlim.TryEnterReadLock(timeOutSecond);
                DataTable dt = new DataTable();
                using (var con = new SQLiteConnection(conStr))
                {
                    dt.Load(con.ExecuteReader(sql));
                }

                return dt;
            }
            catch (Exception e)
            {
                throw e;
            }
            finally
            {
                readerWriterLockSlim.ExitReadLock();
            }
        }

        public void QueryMultiple(string sql, Action callBack)
        {
            try
            {
                readerWriterLockSlim.TryEnterReadLock(timeOutSecond);
                using (var con = new SQLiteConnection(conStr))
                {
                    var reader = con.QueryMultiple(sql);
                    callBack(reader);
                }
            }
            catch (Exception e)
            {
                throw e;
            }
            finally
            {
                readerWriterLockSlim.ExitReadLock();
            }
        }
        #endregion 公共方法



